/** @format */

import { useSelector } from "react-redux";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";

function App() {
	/*To use useSelector we have to pass a funciton and the function receives the 
  react-state automatic because this function will be executed by react-redux
  and then we shold pass the data we want to use in this component and in this case 
  it is the cartIsVisible value and this is  (state.ui.cartIsVisible) the way we extract the value we need
  */
	const showCart = useSelector((state) => state.ui.cartIsVisible);
	return (
		<Layout>
			<Cart />
			<Products />
		</Layout>
	);
}

export default App;
